import React, {useState} from 'react';


function Widget(props) {

const [cityWeather, setWeather] = useState(0);

  return (
    <div>
      Город:
      {props.city}
      Температура:
      {cityWeather}
    </div>
  );

  useEffect(function () {
     axios.get('http://openweathermap.org/data/2.5/weather?q=Moscow&appid=b6907d289e10d714a6e88b30761fae22', {}).then(function(response){
            setWeather(response.city)
       })
  })
}

export default Widget;
