import React, {useState} from 'react';
import './App.css';
import './bootstrap.css';
import Weather from './wethervidget';

function App(props) {

const [city, setCity] = useState('Moscow');

this.cityInput = React.createRef();
function onButton(event) {(event) => {setCity(event.target.value)}};// при клике на кнопку - обновляю city


  return (
    <div className='card mx-auto' style={{width: '50%'}}>
      <div className='card-header'>
      <h3>ПОГОДА</h3>

      <input type='text' ref={this.cityInput} />
      <button onClick = {onButton()}>Узнать погоду</button>
      </div>
      <div className='card-body'>
  <Weather city={city} />    
      </div>
    </div>

  );
}

export default App;


