import React, {useState, useTown} from 'react';
import Weather from './wethervidget';
import './App.css';

function App(props) {

  const [town, setTown] = useState();

  return (
    <div className="App">
      ПОГОДА

  <Weather city = {<input type='text' value={town} onChange = {(event) => {setTown(event.target.value)}}/>} />
    </div>
  );
}

export default App;


