import React, {useState} from 'react';
import './App.css';
import Widget from './wethervidget';

function App(props) {

  const [city, setCity] = useState('Moscow');

  console.log(city);
  return (
    <div className="App">
      ПОГОДА
  <input type='text' value={city} onChange = {(event) => {setCity(event.target.city)}}/>    
  <Widget city />
    </div>

  );
}

export default App;


